#include <iostream>
#include <vector>
using namespace std;
int main() {
  int x, y, N, v;
  cin >> x >> y >> N;cin.ignore();
  string s, a;
  vector<string> S;
  for (int i = 0; i < N; i++) {
    getline(cin, s);
    S.push_back(s);
  }
  while (a.back() != '.' && a.back() != '?') {
    a += S[y][x];
    v = S[y][x];
    y = (y + v % 10) % N;
    x = (x + v / 10) % N;
  }
  cout << a;
}
