grid=[];

const N = parseInt(readline());

for (let i = 0; i < N; i++) {//parsing inputs into grid as a 2d array
    grid[i] = readline().split(" ");
}


newgrid=[];
for(i=0;i<grid.length;i++){newgrid[i]=[]}//creating a new unpopulated 2d array
  
for(col=0;col<grid.length;col++){//swapping rows and cols while looping through old cols backwards
    for(row=0;row<grid.length;row++){//this can also be done seperately by transposing matrix and then flipping horizontaly
        newgrid[row][col]=grid[grid.length-1-col][row] 
    }
}
  
grid=newgrid;
  

for(i=0;i<grid.length;i++){
    console.log(grid[i].join(" "))
}
