/**
 * This code is for creation and verification of test cases.
 * It is designed NOT to be beautiful or super fast.
 * It is designed to provide lots of run-time info to assist design.
 */
import java.io.IOException;
import java.util.ArrayList;
import java.util.Collections;
import java.util.Comparator;
import java.util.LinkedList;
import java.util.List;

public class Solution {
	static double ans;
	static double lim;
	static boolean isDebug = false;
	static boolean isVerbose = false;
	static List<Design> designs = new ArrayList<>();

	public static void main(String[] args) {
		String s = "";

		while ((s = readLine()) != null) {
			if (s.isEmpty()) {
				break;
			}

			lim = Double.parseDouble(s); // room limit

			s = readLine();
			int n = Integer.parseInt(s); // num of weights

			int[] wArr = new int[n];

			s = readLine();
			String[] ss = s.split("\\s");

			for (int i = 0; i < n; i++) {
				int w = Integer.parseInt(ss[i]);
				wArr[i] = w;
			}
			solve(wArr);

			if (ans < 0) {
				System.out.println("-1");
			} else {
				System.out.printf("%.4f\n", ans);
			}

		} // while
	}

	private static void solve(int[] arr) {
		if (lim == 1.0 && arr.length >= 2) {
			ans = 1.0;
			return;
		}

		ans = -1;
		List<Mobile> list1 = new LinkedList<>();
		for (int i = 0; i < arr.length; i++) {
			list1.add(new Mobile(arr[i]));
		}
		search(list1);

		Collections.sort(designs, new Comparator<Design>() {
			@Override
			public int compare(Design o1, Design o2) {
				return Double.compare(o2.d, o1.d);
			}
		});

		if (isVerbose) {
			for (Design d : designs) {
				System.err.println(d);
			}
		}
	}

	// TODO
	// recursive
	private static void search(List<Mobile> list1) {
		if (isDebug)
			showMobileList(list1);

		int len = list1.size();

		if (len == 1) {
			double dd = list1.get(0).dL + list1.get(0).dR;

			// log the design
			Mobile m = list1.get(0); // the only mobile in list
			designs.add(new Design(m, dd));

			if (dd <= lim) {
				ans = Math.max(ans, dd);
			}
			return;
		}

		for (int i = 0; i < len - 1; i++) {
			for (int j = i + 1; j < len; j++) {

				// make a hard copy of list1
				List<Mobile> list2 = new LinkedList<>();
				for (Mobile m : list1) {
					list2.add(m);
				}

				// item i, j are used to build a new item, no use now
				list2.remove(j);
				list2.remove(i);
				list2.add(new Mobile(list1.get(i), list1.get(j)));

				if (isDebug)
					System.err.println("search " + list1.get(i) + " " + list1.get(j));
				search(list2);

				// replace the last item at list2 by this new item
				list2.remove(list2.size() - 1);
				list2.add(new Mobile(list1.get(j), list1.get(i)));

				if (isDebug)
					System.err.println("rev, search again");
				search(list2);
			}
		}
	}

	private static void showMobileList(List<Mobile> list) {
		for (Mobile m : list) {
			System.err.println(m);
		}

		if (isDebug)
			System.err.println("--- ans = " + ans + " ---\n");
	}

	private static String readLine() {
		StringBuffer sb = new StringBuffer();
		int b = 0;
		while (b != '\n' && b >= 0) {
			try {
				b = System.in.read();
			} catch (IOException e) {
				return null;
			}
			if (b != '\r' && b != '\n' && b >= 0)
				sb.append((char) b);
		}
		if (sb.length() == 0 && b < 0) {
			return null;
		}
		return sb.toString().trim();
	}

}

class Mobile {
	double dL = 0.0, dR = 0.0; // distance of rod on two sides
	int w; // total weight (sum of w of sub-mobiles)
	Mobile a = null, b = null;

	public Mobile(Mobile a, Mobile b) {
		this.w = a.w + b.w;

		this.a = a;
		this.b = b;
		double ll = (double) b.w / w;
		double rr = (double) a.w / w;

		this.dL = Math.max(ll + a.dL, b.dL - rr);
		this.dR = Math.max(rr + b.dR, a.dR - ll);
	}

	// when mobile is a leave, a and b are null
	public Mobile(int w) {
		this.w = w;
	}

	StringBuilder tree = new StringBuilder();

	@Override
	public String toString() {
		explore(this, 0);
		return tree.toString();
	}

	// recursive
	private void explore(Mobile m, int sp) {
		if (m.isLeave()) {
			tree.append(space(sp)).append("w=" + m.w).append('\n');
			return;
		}

		tree.append(space(sp))
				.append("w=" + m.w + ", a.w=" + m.a.w + ", b.w=" + m.b.w + ", dL=" + m.dL + ", dR=" + m.dR)
				.append('\n');
		explore(m.a, sp + 1);
		explore(m.b, sp + 1);
	}

	private String space(int n) {
		StringBuilder sb = new StringBuilder();
		for (int i = 0; i < n; i++) {
			sb.append("  ");
		}
		return sb.toString();
	}

	private boolean isLeave() {
		return a == null || b == null;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((a == null) ? 0 : a.hashCode());
		result = prime * result + ((b == null) ? 0 : b.hashCode());
		long temp;
		temp = Double.doubleToLongBits(dL);
		result = prime * result + (int) (temp ^ (temp >>> 32));
		temp = Double.doubleToLongBits(dR);
		result = prime * result + (int) (temp ^ (temp >>> 32));
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Mobile other = (Mobile) obj;
		if (a == null) {
			if (other.a != null)
				return false;
		} else if (!a.equals(other.a))
			return false;
		if (b == null) {
			if (other.b != null)
				return false;
		} else if (!b.equals(other.b))
			return false;
		if (Double.doubleToLongBits(dL) != Double.doubleToLongBits(other.dL))
			return false;
		if (Double.doubleToLongBits(dR) != Double.doubleToLongBits(other.dR))
			return false;
		return true;
	}

}

class Design {
	Mobile m;
	double d;

	public Design(Mobile m, double d) {
		this.m = m;
		this.d = d;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		long temp;
		temp = Double.doubleToLongBits(d);
		result = prime * result + (int) (temp ^ (temp >>> 32));
		result = prime * result + ((m == null) ? 0 : m.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Design other = (Design) obj;
		if (Double.doubleToLongBits(d) != Double.doubleToLongBits(other.d))
			return false;
		if (m == null) {
			if (other.m != null)
				return false;
		} else if (!m.equals(other.m))
			return false;
		return true;
	}

	@Override
	public String toString() {
		return "total width = " + d + "\n" + m;
	}

}
