#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main()
{
    int dist;
    int speed;
    int dogSpeed;
    cin >> dist >> speed >> dogSpeed; cin.ignore();

    // The goal is to try to calculate the position where the dog meets
    // the people, iterating until the people meet.
    // But you can just calculate the time when the people meet, then the
    // distance traveled by the dog during this time.

    // => time = dist / (2*speed), then dogDist = dogSpeed * time
    // => dogDist = dist * dogSpeed / (2*speed)
    // Don't need any unit conversion.
    double dogDist = dist * dogSpeed / (2.0 * speed);

    cout << (int)(dogDist+0.5) << endl;
}
