import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        for (int i = 0; i < N; i++) 
        {
            String fishLine = in.nextLine();
            String[] splitLine = fishLine.substring(3,fishLine.length()-3).split("");
            int rightEaten = 0;
            int leftEaten = 0;
            for (int j = 0; j < splitLine.length; j++)
            {
                if (j < splitLine.length/2)
                {
                    if (splitLine[j].equals("o")) { rightEaten++; }
                }
                else
                {
                    if (splitLine[j].equals("o")) { leftEaten++; }
                }
            }
            System.out.println(rightEaten + " " + leftEaten);
        }
    }
}

