import sys
import math

codyspeed = int(input())
parsyspeed = int(input())
duratiion = int(input())

encounters = 0
cody = []
parsy = []

time_in_minutes = (duratiion*60)

for i in range(codyspeed, time_in_minutes):
    if i % codyspeed == 0:
        cody.append(i)

for i in range(parsyspeed, time_in_minutes):
    if i % parsyspeed == 0:
        parsy.append(i)

for i in cody:
    if i in parsy:
        encounters += 1
    else:
        pass

if encounters > 0:
    print(encounters)
else:
    print("No Encounters")
