const N = parseInt(readline());
const values = readline().split(' ');

console.error(values);

console.log(values.map((val, idx) => {
    if (idx === N - 1)
        return val;
    const v1 = eval(val);
    const v2 = eval(values[idx + 1]);
    let rel = '=';
    if (v1 > v2)
        rel = '>';
    if (v1 < v2)
        rel = '<';
    return `${val} ${rel}`;
}).join(' '));
