const word = readline().toLowerCase();
const n = parseInt(readline());
const sentences = [];
for (let i = 0; i < n; i++) {
   sentences.push(readline().toLowerCase());
}
const occurrences = sentences.map((sentence) => {
   return [...sentence.matchAll(/(\w)+/gi)]
   .map((regex)=>regex[0])
   .filter((w)=>w===word).length
});
console.log(occurrences.reduce((t,c)=>t+c, 0) + ' ' + occurrences.filter((v)=>v).length);
