def shortest():
    print(hex(int(input()))[2:].upper().replace("0","O"))

def algorithm():
    # Get the input
    n = int(input())

    # Handle the number 0 as a special case
    if n == 0:
        print("O")
        return

    # Decode into hexadecimal. The resulting word is reversed
    base = 16
    numbers = [0, 0, 0, 0, 0, 0, 0, 0]
    size = 0
    while n > 0:
        numbers[size] = n % base
        size += 1
        n = n // base

    # Convert the numbers into letters
    for i in range(size - 1, -1, -1):
        if numbers[i] == 10:
            print("A", end="")
        elif numbers[i] == 11:
            print("B", end="")
        elif numbers[i] == 12:
            print("C", end="")
        elif numbers[i] == 13:
            print("D", end="")
        elif numbers[i] == 14:
            print("E", end="")
        elif numbers[i] == 15:
            print("F", end="")
        elif numbers[i] == 0:
            print("O", end="")
        else:
            print("?", end="")

algorithm()
