CARD_SCORES = {'A': 14, 'K': 13, 'Q': 12, 'J': 11, 'T': 10}
STRAIGHT = 'A23456789TJQKA'

def score(card):
    return int(card) if card.isdigit() else CARD_SCORES[card]

n = int(input())
for _ in range(n):
    hand = ''.join(sorted(set(input().split()), key=score))
    if len(hand) < 5:
        print("false")
    elif hand in STRAIGHT:
        print("true")
    elif 'A' in hand and 'A'+hand[:4] in STRAIGHT:
        print("true")
    else: 
        print("false")
