int main()
{
    int stadiumLength;
    int stadiumWidth;
    int turtleSpeed;
    int rabbitSpeed;
    int duration;
    cin >> stadiumLength >> stadiumWidth >> turtleSpeed >> rabbitSpeed >> duration; cin.ignore();

    int dist = stadiumLength*2 + (stadiumWidth-2)*2;
    int rabToFinish = std::min(
            (rabbitSpeed*duration)%dist,
            dist - (rabbitSpeed*duration)%dist
    );
    int turToFinish = std::min(
            (turtleSpeed*duration)%dist,
            dist - (turtleSpeed*duration)%dist
    );
    cerr << rabToFinish << " " << turToFinish << endl;
    if(turToFinish == rabToFinish) {
        cout << "DRAW" << endl;
    } else if (turToFinish < rabToFinish) {
        cout << "TURTLE" << endl;
    } else {
        cout << "RABBIT" << endl;
    }

}
