#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;
int main(){
  int N;
  cin>>N; cin.ignore();
  string s;
  vector<string> S(N),R(6);
  for(int i=0;i<N;i++)getline(cin,S[i]);
  R[0]=S[0];
  R[1]=S.back();
  for(int i=0;i<N;i++){
    R[2]+=S[i][0];
    R[3]+=S[i][N-1];
    R[4]+=S[i][i];
    R[5]+=S[i][N-1-i];}
  sort(begin(R),end(R));
  for(int i=0;i<6;i++)cout<<R[i]<<endl;}
