using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        string initials = "UVABCDEFGHIJLMNOPRST";
        var births = new int[20];
        for (int i = 0; i < N; i++)
        {
            string name = Console.ReadLine();
            births[initials.IndexOf(name[0])]++;
        }

        for (int i = 0; i < 20; i++)
        {
            if(births[i] > 0)
            {
                Console.WriteLine((i+2003)+" "+births[i]);
            }
        }
    }
}
