#include <algorithm>
#include <iostream>
#include <vector>
using namespace std;
int main() {
  int N;
  cin >> N;
  cin.ignore();
  vector<string> S;
  for (int i = 0; i < N; i++) {
    string s, x;
    getline(cin, s);
    int ind = s.size() - 1;
    while (x.size() < 2) {
      if (isalpha(s[ind]))x += s[ind];
      ind--;
    }
    auto it = find(S.begin(), S.end(), x);
    if (it != S.end())cout << char('A' + it - S.begin());
    else {
      S.push_back(x);
      cout << char('A' + S.size() - 1);
    }
  }
}
