import numpy as np

word_1 = raw_input()
word_2 = raw_input()

def levenshteinDist(str1, str2):
    len1, len2 = len(str1), len(str2)
    d = np.zeros((len1+1, len2+1))
    for i in range(len1+1):
        d[i, 0] = i
    for j in range(len2+1):
        d[0, j] = j
    for i in range(1,len1+1):
        for j in range(1,len2+1):
            substitution_cost = 0 if str1[i-1] == str2[j-1] else 1
            d[i, j] = min(
                d[i-1, j] + 1,
                d[i, j-1] + 1,
                d[i-1, j-1] + substitution_cost
            )
    return int(d[len1, len2])

print(levenshteinDist(word_1, word_2))
