let r = readline;
let n = parseInt(r());
let digits = [];

while(n--)
{
    let ipt = r().split(" ");
    let value1 = parseInt(ipt[0]);
    let operation = ipt[1];
    let value2 = parseInt(ipt[2]);

    if(operation == '>')
        digits.push(value1 > value2);

    if(operation == '<')
        digits.push(value1 < value2);

    if(operation == '!=')
        digits.push(value1 != value2);

    if(operation == '==')
        digits.push(value1 == value2);

    if(operation == '<=')
    digits.push(value1 <= value2);

    if(operation == '>=')
    digits.push(value1 >= value2);
}


console.log(parseInt(digits.reverse().map(x=>x==true?1:0).join(""),2));
