using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{
	public static char dot = '.';
	public static char[,] Grid;
	public static int MidPt;
	public static int siz;

	public static void Main()
	{
        siz = int.Parse(Console.ReadLine());

        int squareSiz = siz * siz;
        MidPt = siz/2;

        Grid = new char[siz,siz];

        for (int lin = 0 ; lin < siz ; lin++)
        {
            string aLine = Console.ReadLine();
            for (int pos = 0 ; pos < siz ; pos++ )
            {
                Grid[lin,pos] = aLine[pos];
            }
        }

        List<int> SUMS = new List<int>();
        for (int QUAD = 1 ; QUAD <= 4 ; QUAD++)
        {
            int SUM= GetSumForThisQuadrant(QUAD);
            SUMS.Add(SUM);
        }

        var SINGLE = SUMS.GroupBy(x => x).Where(x => x.Count() == 1).SelectMany(x => x).ToList();
        int ODDSUM = SINGLE[0];
        var STANDARD = SUMS.GroupBy(x => x).Where(x => x.Count() == 3).SelectMany(x => x).ToList();

        int STANDARDSUM = STANDARD[0];

        for (int QUAD = 1 ; QUAD <= 4 ; QUAD++)
        {
            int SUM = GetSumForThisQuadrant(QUAD);
            if (SUM == ODDSUM)
            {
                Console.WriteLine($"Quad-{QUAD} is Odd-Quad-Out");
            }
        }
        Console.WriteLine($"It has value of {ODDSUM}");
        Console.WriteLine($"Others have value of {STANDARDSUM}");

	}

	public static int GetSumForThisQuadrant(int whichQuad)
	{

		int sum = 0;
		int linMin = 0 ;  int linMax = siz;		
		int posMin = 0 ;  int posMax = siz;
		string description = "topLeft";
		if (whichQuad == 2) description = "topRight";
		if (whichQuad == 3) description = "bottomLeft";
		if (whichQuad == 4) description = "bottomRight";

		if (description.Contains("top"))    linMax = MidPt;
		if (description.Contains("bottom")) linMin = MidPt;
		if (description.Contains("Left"))   posMax = MidPt;
		if (description.Contains("Right"))  posMin = MidPt;

		for (int lin = linMin ; lin < linMax ; lin++)
		{
			for (int pos = posMin ; pos < posMax ; pos++)
			{
				int ch = Grid[lin,pos];
				if (ch != dot) sum += (ch - '0');
			}
		}
		return sum;
	}
}
