use std::io;

macro_rules! parse_input {
    ($x:expr, $t:ident) => ($x.trim().parse::<$t>().unwrap())
}

fn main() {
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let width = parse_input!(input_line, i32);
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let height = parse_input!(input_line, i32);
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let t = parse_input!(input_line, i32);

    let mut y_pos: i32 = 0;
    let mut grid: Vec<Vec<i32>> = vec![];
    for i in 0..height as usize {
        let mut input_line = String::new();
        io::stdin().read_line(&mut input_line).unwrap();
        let line = input_line.trim().to_string();
        grid.push(vec![]);
        let mut negative = 1;
        for c in line.chars() {
            match c {
                '.' => grid[i].push(0),
                '-' => negative = -1,
                'V' => {
                    y_pos = i as i32;
                    grid[i].push(0);
                },
                c => {
                    grid[i].push(negative * (c as u8 - '0' as u8) as i32);
                    negative = 1;
                }
            }
        }
    }

    let mut x_pos: i32 = 0;
    for x in 0..t as usize {
        eprintln!("{} {}", x_pos, y_pos);
        let mut new_y_pos: i32 = y_pos;
        new_y_pos += 1;
        new_y_pos -= grid[new_y_pos as usize][x + 1];

        if new_y_pos < 0 || new_y_pos > height - 1 {
            eprintln!("break {} {}", x_pos, new_y_pos);
            break;
        }

        y_pos = new_y_pos;
        x_pos += 1;
    }

    println!("{} {}", x_pos, height - 1 - y_pos);
}
