let input = readline();         // Read the input
const VOWELS = ['a', 'e', 'i', 'o', 'u'];
let checkDouble = 0;            // Flag to check if there are consecutive vowels
let result = '';
for (let letter of input){      // Iterate over each char of input
    if (VOWELS.includes(letter) && !checkDouble){       
        result += 'ub' + letter; 
        checkDouble = 1;
    } else{
        result += letter;
        if (!VOWELS.includes(letter)) checkDouble = 0;
    }


}

console.log(result);
