#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

const int dirsCount = 4;
const int rowChange[dirsCount] = { -1,  1,  0,  0 }; // UDLR
const int colChange[dirsCount] = {  0,  0, -1,  1 }; // UDLR

int w;
int h;
int puzzle[20][20];

bool isFloatingPart(const int rowIdx, const int colIdx) {
	bool floating = true;
	for (int dirIdx = 0; dirIdx < dirsCount; ++dirIdx) {
		const int newRowIdx = rowIdx + rowChange[dirIdx];
		const int newColIdx = colIdx + colChange[dirIdx];
		if (newRowIdx >= 0 && newRowIdx < h && newColIdx >= 0 && newColIdx < w) {
			if (puzzle[newRowIdx][newColIdx]) {
				floating = false;
				break;
			}
		}
	}

	return floating;
}

int main() {
	cin >> w >> h; cin.ignore();
	for (int rowIdx = 0; rowIdx < h; ++rowIdx) {
		string puzzle_row;
		getline(cin, puzzle_row);

		for (int colIdx = 0; colIdx < w; ++colIdx) {
			puzzle[rowIdx][colIdx] = '1' == puzzle_row[colIdx];
		}
	}

	int floatingPartsCount = 0;
	for (int rowIdx = 0; rowIdx < h; ++rowIdx) {
		for (int colIdx = 0; colIdx < w; ++colIdx) {
			if (!puzzle[rowIdx][colIdx]) {
				floatingPartsCount += isFloatingPart(rowIdx, colIdx);
			}
		}
	}

	cout << floatingPartsCount << endl;
    return 0;
}
