// Read inputs from Standard input.
// Write outputs to Standard output.

using System;
using System.IO;
using System.Text;
using System.Linq;
using System.Collections;
using System.Collections.Generic;

class Solution
{
	static void Main(String[] args)
	{
		int N = int.Parse(Console.ReadLine());
		List<string> Actions = new List<string>();
		for (int i = 0; i < N; i++)
		{
		    string action = Console.ReadLine();
		    Actions.Add(action);
		}
		int nbOrders = int.Parse(Console.ReadLine());
		Dictionary<string, HashSet<string>> domino = new Dictionary<string, HashSet<string>>();
		for (int i = 0; i < nbOrders; i++)
		{
		    string[] input = Console.ReadLine().Split(' ');
		    string a1 = input[0];
		    string precedence = input[1];
		    string a2 = input[2];

		    if (precedence=="after")
		    {
		        string temp = a1;
		        a1 = a2;
		        a2 = temp;
		    }

	        if (!domino.ContainsKey(a1))
	        {
	            domino.Add(a1, new HashSet<string>());
	        }
            domino[a1].Add(a2);
		}

		string first = String.Empty;
		while(Actions.Any())
		{
	        var possibilities = domino.Keys.Where(k => k != first & !domino.Values.Any(v => v.Contains(k))).ToList();
		    if (domino.ContainsKey(first))
		    {
		        var other = domino[first].Where(v => !domino.Any(d => d.Key != first && d.Value.Contains(v))).ToList();
	            possibilities.AddRange(other);
	            domino.Remove(first);
		    }
    		// Actions not ordered
    		var notOrdered = Actions.Where(a => !domino.ContainsKey(a) && !domino.Values.Any(v => v.Contains(a))).ToList();
    		if (notOrdered != null)
    		{
    		    possibilities.AddRange(notOrdered);
    		}
	        string next = First(possibilities, Actions);
	        Console.WriteLine(next);
	        Actions.Remove(next);
	        first = next;
		}
	}

	public static string First(List<string> data, List<string> order)
	{
	    string result = data.First();
	    int index = order.Count;
	    foreach (string s in data)
	    {
	        for (int i = 0; i < index; i++)
	        {
	            if (s == order[i])
	            {
	                result = s;
	                index = i;
	                break;
	            }
	        }
	    }
	    return result;
	}
}
