const S = readline();

const VOWELS = "aeiouAEIOU".split("")

function reverse(string) {
  const chars = string.split("");
  const vowels = [];
  let result = "";

  chars.forEach(c => {
    if (VOWELS.indexOf(c) !== -1) {
      vowels.push(c);
    }
  });

  chars.forEach(c => {
    if (VOWELS.indexOf(c) !== -1) {
      result += vowels.pop();
    } else {
      result += c;
    }

  })

  return result;
}

