program FightorFlight;
{$H+}
uses sysutils, classes, math;

// Helper to read a line and split tokens
procedure ParseIn(Inputs: TStrings) ;
var Line : string;
begin
    readln(Line);
    Inputs.Clear;
    Inputs.Delimiter := ' ';
    Inputs.DelimitedText := Line;
end;

var
    monsterHealth : Int32;
    monsterAttack : Int32;
    monsterElement : string;
    aiHealth : Int32;
    aiAttack : Int32;
    aiElement : string;
    Inputs: TStringList;
begin
    Inputs := TStringList.Create;
    ParseIn(Inputs);
    monsterHealth := StrToInt(Inputs[0]);
    ParseIn(Inputs);
    monsterAttack := StrToInt(Inputs[0]);
    ParseIn(Inputs);
    monsterElement := (Inputs[0]);
    ParseIn(Inputs);
    aiHealth := StrToInt(Inputs[0]);
    ParseIn(Inputs);
    aiAttack := StrToInt(Inputs[0]);
    ParseIn(Inputs);
    aiElement := (Inputs[0]);
    while (monsterHealth > 0) and (aiHealth > 0) do
    begin
        if (monsterElement = aiElement) then
        begin
            monsterHealth := monsterHealth - aiAttack;
            aiHealth := aiHealth - monsterAttack;
        end
        else if ((monsterElement = 'fire') and (aiElement = 'water')) or ((monsterElement = 'ice') and (aiElement = 'fire')) 
        or ((monsterElement = 'water') and (aiElement = 'ice')) then
        begin
            monsterHealth := monsterHealth - (aiAttack*2);
            aiHealth := aiHealth - monsterAttack;
        end
        else
        begin
            monsterHealth := monsterHealth - (aiAttack);
            aiHealth := aiHealth - (monsterAttack*2);
        end;
    end;
    if aiHealth > monsterHealth then
    writeln('Fight')
    else writeln('Flight');
    writeln(aiHealth);
end.
