def shift_letter(letter, shift):
    if 'a' <= letter <= 'z':
        return chr((ord(letter) - ord('a') + shift) % 26 + ord('a'))
    elif 'A' <= letter <= 'Z':
        return chr((ord(letter) - ord('A') + shift) % 26 + ord('A'))
    else:
        return letter

sentence = input()
transformed_words = [''.join([shift_letter(char, 2) for char in word][::-1]) for word in sentence.split()]
print(' '.join(transformed_words))
