#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;
int N,r=0,XX[100][100];
int common_letters(string &s1,string &s2){
  int x=0;
  vector<int> f1(26),f2(26);
  for(int i=0;i<s1.size();i++)f1[s1[i]-97]++;
  for(int i=0;i<s2.size();i++)f2[s2[i]-97]++;
  for(int i=0;i<26;i++)x+=min(f1[i],f2[i]);
  return x;}
void rec(vector<int> A,vector<int> &S){
  vector<int> X;
  for(int i=0;i<S.size();i++){
    if(XX[A.back()][S[i]])X.push_back(S[i]);}
  if(X.size()==0 || A.size()+X.size()<=r){
    if(A.size()>r)r=A.size();
    return;}
  A.push_back(0);
  while(X.size()){
    A.back()=X[0];
    X.erase(X.begin());
    rec(A,X);}}
int main(){
  cin>>N;cin.ignore();
  vector<string> S(100);
  vector<vector<int>> SS(100);
  for(int i=0;i<N;i++){
    getline(cin,S[i]);
    transform(begin(S[i]),end(S[i]),begin(S[i]),::tolower);}
  for(int i=0;i<N;i++){
    for(int j=i+1;j<N;j++){
      if(common_letters(S[i],S[j])<3){
        XX[i][j]=1;
        SS[i].push_back(j);}}}
  for(int i=0;i<N;i++){
    vector<int> A(1,i);
    rec(A,SS[i]);}
  cout<<r;}
