# -*- coding: utf-8 -*-
"""
Created on Tue Oct 31 08:01:53 2023

@author: muthu
"""
import math

v1 = input()
v2 = input()

v1 = "".join(v1.split())
v2 = "".join(v2.split())

co_v1, co_v2 = [0,0,0], [0,0,0]
co = 0
sign = "+"


for a in v1:
    try:
        co = co*10+int(a)
    except:
        if a=="i":
            if co==0:
                co=1
            co_v1[0] = int(sign+str(co))
            co = 0
            sign = "+"
        if a=="j":
            if co==0:
                co=1
            co_v1[1] = int(sign+str(co))
            co = 0
            sign = "+"
        if a=="k":
            if co==0:
                co=1
            co_v1[2] = int(sign+str(co))
            co = 0
            sign = "+"
        else:
            sign=a

co = 0
sign = "+"
for a in v2:
    try:
        co = co*10+int(a)
    except:
        if a=="i":
            if co==0:
                co=1
            co_v2[0] = int(sign+str(co))
            co = 0
            sign = "+"
        if a=="j":
            if co==0:
                co=1
            co_v2[1] = int(sign+str(co))
            co = 0
            sign = "+"
        if a=="k":
            if co==0:
                co=1
            co_v2[2] = int(sign+str(co))
            co = 0
            sign = "+"
        else:
            sign=a

i1,j1,k1 = co_v1[0], co_v1[1], co_v1[2]
i2,j2,k2 = co_v2[0], co_v2[1], co_v2[2]
distance = round(math.sqrt((i1-i2)**2+(j1-j2)**2+(k1-k2)**2), 2)
_dir1 = [i2-i1,j2-j1,k2-k1]
dir_vec=""

def solve(nums):
   if len(nums) == 1:
      return nums

   div = math.gcd(nums[0], nums[1])

   if len(nums) == 2:
      return [nums[0]//div,nums[1]//2]

   for i in range(1, len(nums) - 1):
      div = math.gcd(div, nums[i + 1])
      if div == 1:
         return nums
   k = []
   for a in nums:
       k.append(a//div)
   return k

_dir = solve(_dir1)
for i in range(0,3):
    if i ==0:
        if _dir[i]==1:
            dir_vec+="i"
        if _dir[i]==-1:
            dir_vec+="-i"
        if _dir[i]!=0 and abs(_dir[i])!=1:
            dir_vec+=str(_dir[i])+"i"
    if i == 1:
        if _dir[i]==1:
            dir_vec+="+j"
        if _dir[i]==-1:
            dir_vec+="-j"
        if _dir[i]!=0 and abs(_dir[i])!=1:
            if _dir[i]>0:
                j_sign="+"
            if _dir[i]<0:
                j_sign=""
            dir_vec+=j_sign+str(_dir[i])+"j"

    if i == 2:
        if _dir[i]==1:
            dir_vec+="+k"
        if _dir[i]==-1:
            dir_vec+="-k"
        if _dir[i]!=0 and abs(_dir[i])!=1:
            if _dir[i]>0:
                k_sign="+"
            if _dir[i]<0:
                k_sign=""
            dir_vec+=k_sign+str(_dir[i])+"k"        

if dir_vec[0]=="+":
    print("Direction:",dir_vec[1:])
else:
    print("Direction:",dir_vec)
print("Distance:",distance)
