def sum_digits(x,n=10):
    l = []
    i = 0
    while n**i<x: i+=1
    while x!=0:
        l.append(x//(n**i))
        x %= n**i
        i -= 1
    return sum(l)

def buzzle(x,n=10,num=[7]):
    for i in num:
        if x%i==0 or x%n==i: return True
    if x<n: return False # One single digit
    else: return buzzle(sum_digits(x,n),n,num) # Several digits -> testing sum of the digits


