import re

def repl(m: re.Match) -> str:
    words = m.group(0).split()
    return words[0].casefold() + ''.join(
        word[0].upper() + word[1:].casefold()
        for word in words[1:]
    )

text = input().replace("'", "").replace("-", " ")
text = re.sub(r'[A-Za-z][A-Za-z\s]+', repl, text)

print(text)
