l=input().split()
try:
    if "INR"==l[1]:
        ica=float(l[0])
        fca=float(l[3])
        fcn=l[4]
        m=1.0
        if fcn=="USD":m=83.25
        elif fcn=="CNY":m=11.38
        elif fcn=="EUR":m=88.48
        actualica=m*fca
        if actualica>ica:
            print(f"Following the conversion rate of 1 {fcn} = {m} INR, the exchange amount is less by {actualica-ica} INR :(")
        elif actualica<ica:
            print(f"Following the conversion rate of 1 {fcn} = {m} INR, the exchange amount is more by {ica-actualica} INR.")
        else:
            print(f"Following the conversion rate of 1 {fcn} = {m} INR, the exchange amount is ok :)")
    else:
        print("Invalid input.")
except:
    print("Invalid input.")
