//Yes, it's long, I know

var card1 = readline().split(' ');
var card2 = readline().split(' ');

Array.prototype.remove = function(value) {
    const index = this.findIndex((e) => e === value);
    if (index !== -1)
        this.splice(index, 1);
}

function getThirdAttributeValue(values, value1, value2) {
    values.remove(value1);
    values.remove(value2);
    if (values.length == 1)
        return values[0];
    else return value1;
}

let numbers = ["1", "2", "3"];
let shapes = ["oval", "diamond", "squiggle"];
let colours = ["red", "green", "purple"];
let shadings = ["empty", "shaded", "full"];

let output = "";

output += getThirdAttributeValue(numbers, card1[0], card2[0]);
output += " ";
output += getThirdAttributeValue(shapes, card1[1], card2[1]);
output += " ";
output += getThirdAttributeValue(colours, card1[2], card2[2]);
output += " ";
output += getThirdAttributeValue(shadings, card1[3], card2[3]);

console.log(output);

