width = int(input())
picture =[]
for j in range(5):
    row = input()
    picture.append(row)

dble = 0

for i in range(1, width-1):
    if picture[2][i] == 'C':
        bond = 0

        if picture[2][i-1] == '=':
            bond += 2
            dble += 1
        elif picture[2][i-1] == '-':
            bond += 1
        if picture[2][i+1] == '=':
            bond += 2
            dble += 1
        elif picture[2][i+1] == '-':
            bond += 1
        if picture[1][i] == '|':
            bond += 1
        if picture[3][i] == '|':
            bond += 1

        if bond != 4:
            print("ERROR")
            quit()

if dble == 0:
    print("ALKANE")
elif dble == 2: # Double counting!
    print("ALKENE")
else:
    print("OTHER")
