const N = parseInt(readline());
const S = readline();

let res = ''
for (let i in S) {
    const code = S.toLowerCase().charCodeAt(i)
    if(code === 32) { // if characeter is a space, he does'nt change
        res += ' '
    } else {
        let decoded = i % 2 === 0 ? code + N : code - N
        if(decoded < 97) { // if decoded is less than a whe add 26
            decoded += 26
        }
        if(decoded > 122) { // if the decoded character is greater than z we subtract 26
            decoded -= 26
        }
        const decodedChar = String.fromCharCode(decoded);
        // if the code of the original character is less than 97 (therefore uppercase) we put the decoded character in uppercase
        res = S.charCodeAt(i) < 97 ? res + decodedChar.toUpperCase() : res + decodedChar;
    }
}
console.log(res);
