import java.util.*;

class Solution {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        int[][] mat = new int[n][n];
        int i = 0;
        int j = n/2;
        mat[i][j] = 1;
        for (int k=2;k<=n*n;k++) {
            int row = i;
            int col = j;
            if (i-1<0) {
                row = n - 1;
            } else {
                row = i - 1;
            }
            col = j + 1;
            if (col >= n) {
                col = 0;
            }
            if (mat[row][col]!=0) {
                row = i + 1;
                col = j;
            }
            mat[row][col] = k;
            i = row;
            j = col;
        }
        for (int[] a: mat) {
            for (int b=0;b<a.length;b++) {
                if (b==a.length-1) {
                    System.out.print(a[b]);
                } else {
                    System.out.print(a[b]+" ");
                }
            }
            System.out.println();
        }
    }
}
