class Rect:
    def __init__(self, line):
        self.left_x, self.top_y, self.right_x, self.bottom_y = list(map(int,line.split()))

    def intersection(self, other):
        left_x = max(self.left_x, other.left_x)
        right_x = min(self.right_x, other.right_x)
        top_y = max(self.top_y, other.top_y)
        bottom_y = min(self.bottom_y, other.bottom_y)
        if right_x <= left_x or bottom_y <= top_y:
            return "Empty"
        else:
            return f"{left_x} {top_y} {right_x} {bottom_y}"

rect1 = Rect( input() )
rect2 = Rect( input() )
print( rect1.intersection(rect2) )
