using System; 

public class Program
{
	public static void Main()
	{
		int howMany = int.Parse(Console.ReadLine());

		double d = Math.Sqrt(howMany);
		int roundedUp = (int)Math.Ceiling(d);
		int leftSpots = roundedUp * roundedUp - howMany;

		string ans = $"{howMany} will fit in a square tray with a side-length of {roundedUp}, with {leftSpots} spot(s) left over";

		if (leftSpots == 0)
		{
			ans = $"{howMany} will fit PERFECTLY in a square tray with a side-length of {roundedUp}";
		}

		Console.WriteLine(ans);
	}
}
