import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String s = in.nextLine().toLowerCase();
        int n = in.nextInt();
        HashMap<Character, Integer> map = new HashMap<>();
        ArrayList<Character> listCharacter = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            Character c = in.next().charAt(0);
            System.err.println(c);
            map.put(c, 0);
            listCharacter.add(c);
        }
        for(int i = 0; i < s.length(); i++) {
            map.computeIfPresent(s.charAt(i), (key, val) -> val + 1);
        }
        for(char c : listCharacter) {
            System.out.println((int)Math.ceil((double)map.get(c)/s.length()*100) + "%");
        }
    }
}
