int main()
{
    unsigned char K; //The key represented as a single byte
    scanf("%d", &K);
    int N; //The length of the packet in bytes, not including the checksum
    scanf("%d", &N);
    scanf("\n"); //Ignore the next character to be read in
    unsigned char P[N+1]; //An array for the packet, including an extra space for the checksum
    int sum;
    char nextByte[5] = {'0','x','0','0','\0'}; // A holding space for the next hexadecimal number
    for(int i=0; i<=N; i++)
    {
        scanf("%c%c", &nextByte[2],&nextByte[3]);
        sscanf(nextByte, "%x", &P[i]); //converting from hex in a char array to a single char
        sum+=P[i];
    }
    sum-=P[N]; //Take away the checksum value

    if(sum%K==P[N])
    {
        P[N]='\0'; //Replace the checksum with the null character for printing to standard out
        printf("%s\n", P);
    }
    else
    {
        printf("ERROR\n");
    }
    return 0;
}
