using System;

class Solution
{
    static void Main(string[] args)
    {
        int crewmate = int.Parse(Console.ReadLine());
        int time = int.Parse(Console.ReadLine());
        int cooldown = int.Parse(Console.ReadLine());
        int num = 0;
            for (int i = 0; i < time; i++)
            {
                if (i % cooldown == 0)
                {
                    num++;
                }
            }
            bool more = num >= crewmate;
            Console.WriteLine(more);
            if (more)
                Console.WriteLine(time - crewmate * cooldown);
            else
                Console.WriteLine(crewmate * cooldown - time);
    }
}
