N = int(input())
word = input()

permutations = []
permutations.append(word[0])

for i in range(1, len(word)):
     for j in reversed(range(len(permutations))):
        current = permutations.pop(j)
        for k in range(len(current) + 1):
            permutations.append(current[:k] + word[i] + current[k:])


print(*sorted(list(set(permutations))))
