int shortest_steps_to_num(int num) {

    int step_number = 0;

    while(num != 1)
    {
      if(0 == (num % 2))
      {
        num >>= 1;
      }
      else
      {
        num -= 1;
      }
      step_number++;
    }

    return step_number;
}

int main(){

    int num;
    scanf("%d", &num);

    printf("%d", shortest_steps_to_num(num));
}
