#Wonotonimo solution in python
import sys
import numpy as np

forest = np.array([ [a for a in input()] for _ in range(6)])
fire=0
def debug(forest):
    global fire
    for x in range(6):
        for y in range(6):
            if forest[x,y]=='*':
                fire+=1

            print(end=forest[x,y], file=sys.stderr, flush=True)
        print("", file=sys.stderr, flush=True)

def chop(x,y,forest):
    if forest[x,y] != "#":
        return 0

    for i in range(-2,3):
        for j in range(-2,3):
            if (i!=0 or j!=0) and \
                x+i >= 0 and x+i < 6 and \
                y+j >=0 and y+j < 6 and \
                forest[x+i,y+j] == "*":
                    forest[x,y] = "="
                    return 1
    return 0

debug(forest)
print(" === After ===", file=sys.stderr, flush=True)

total_cuts = 0
for x in range(6):
    for y in range(6):
        total_cuts += chop(x,y,forest)

debug(forest)

tree_count = 0
for x in range(6):
    for y in range(6):
        if forest[x,y] == "#":
            tree_count += 1
if fire==0:
    print('RELAX')
elif tree_count > 0:
    print(total_cuts)
else:
    print('JUST RUN')


