import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);

        int n = in.nextInt();
        if (in.hasNextLine()) in.nextLine();

        List<List<Integer>> roomList = new ArrayList<>();

        for (int i = 0; i < n; i++) {
            List<Integer> keyList = new ArrayList<>();

            for (String field : in.nextLine().split(" +")) {
                keyList.add(Integer.parseInt(field));
            }

            roomList.add(keyList);
        }

        in.close();

        LinkedList<Integer> ownedKeys = new LinkedList<>();
        Set<Integer> visited = new HashSet<>();

        visited.add(0);
        ownedKeys.addAll(roomList.get(0));

        while (visited.size() < n && !ownedKeys.isEmpty()) {
            int key = ownedKeys.remove();

            if (!visited.add(key)) {
                continue;
            }

            ownedKeys.addAll(roomList.get(key));
        }        

        System.out.println(n - visited.size());
    }
}
