'using strict';

let n = Number(readline()),
    tree = {};

for (let i = 0; i < n; i++) {
    let line = readline();
    addLine(line);

    if (line.indexOf('Automaton2000') > -1) {
        print(say('__START__', 0).trim());    
    }
}

function addLine(line) {
    // Remove time infos
    line = line.substring(11);

    // Remove Magus:
    line = line.substring(line.indexOf(':') + 1);

    // Split and remove ignored words
    line = line.split(' ').filter(word => !!word && word != 'Automaton2000');

    addWord('__START__', line[0]);

    for (let i = 1; i < line.length; ++i) {
        addWord(line[i - 1], line[i]);   
    }

    addWord(line[line.length - 1], '__END__');
}

function addWord(previous, next) {
    if (!tree[previous]) {
        tree[previous] = {};   
    }

    if (tree[previous][next] === undefined) {
        tree[previous][next] = 0;
    }

    tree[previous][next] += 1;
}

function say(word, depth) {
    if (depth >= 30) {
        return '';
    }

    let max;

    // Look for the max
    for (let next in tree[word]) {
        if (max === undefined || tree[word][next] > tree[word][max] || (tree[word][next] === tree[word][max] && next < max)) {
            max = next;   
        }
    }

    if (max === '__END__') {
        return '';
    }

    return max + ' ' + say(max, depth + 1);
}
