import java.io.File;
import java.io.FileNotFoundException;
import java.time.Duration;
import java.util.ArrayList;
import java.util.Scanner;

class Step{
	public Step(String from, String to, float distance) {
		super();
		this.from = from;
		this.to = to;
		this.distance = distance;
	}
	String from;
	String to;
	float distance;
}

class StepList extends ArrayList<Step>{
	float getTotalDistance() {
		float returnVal = 0;
		for(Step s: this) returnVal+=s.distance;
		return returnVal;
	}

	float getTotalDistanceHighwayForTrain() {
		float returnVal = 0;
		for(Step s: this) if( s.distance > 6) returnVal+=(s.distance-6);
		return returnVal;
	}

	float getTotalDistanceInTownForTrain() {
		float returnVal = 0;
		for(Step s: this) returnVal+=(s.distance>=6)?6:s.distance;
		return returnVal;
	}

	float getTotalDistanceInTownForCar() {
		float returnVal = 0;
		for(Step s: this) returnVal+=(s.distance>=14)?14:s.distance;
		return returnVal;
	}

	float getTotalDistanceHighwayForCar() {
		float returnVal = 0;
		for(Step s: this) if( s.distance > 14) returnVal+=(s.distance-14);
		return returnVal;
	}
}

public class Solution {
    public static void main(String args[]) {

    	Scanner in = new Scanner(System.in);

        String trip = in.nextLine();
        String startingPoint = trip.split(" ")[0];
    	String destination = trip.split(" ")[1];
    	StepList allSteps = new StepList(); 
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        for (int i = 0; i < N; i++) {
        	 String[] line = in.nextLine().split(" ");
             allSteps.add(new Step(line[0], line[1], Float.parseFloat(line[2])));
        } 	
        StepList steps = buildCorrectRoute(allSteps, startingPoint, destination); 

        /* compute duration for CAR */
        float averageSpeedForCarInTown = 60/(float)50;
        float averageSpeedForCarInHighway = 60/(float)105;
        float timeCar = ( steps.getTotalDistanceInTownForCar() * averageSpeedForCarInTown ) + (steps.getTotalDistanceHighwayForCar() * averageSpeedForCarInHighway); 

        /* compute duration for TRAIN */
        float timeStartingPointToTrainStation = 30;
        float timeTrainStationToDestination = 35;
        float timeForBreakByStep = 8;
        float averageSpeedForTrain = 60/(float)284;
        float averageSpeedForTrainAroundStation = 60/(float)50;
        float timeTrain = timeStartingPointToTrainStation +
        		(timeForBreakByStep * (steps.size()-1)) +
        		(steps.getTotalDistanceHighwayForTrain() * averageSpeedForTrain) +
        		(steps.getTotalDistanceInTownForTrain() * averageSpeedForTrainAroundStation) +
        		timeTrainStationToDestination;

        /* comparaison des temps */
        //System.err.println("temps voiture:"+timeCar+" VERSUS temps train:"+timeTrain);
        Duration dt = Duration.ofMinutes( (long)timeTrain );
        Duration dc = Duration.ofMinutes( (long)timeCar );    
        String minutest = (dt.toMinutesPart()<10)?"0"+dt.toMinutesPart():String.valueOf(dt.toMinutesPart());
        String minutesc = (dc.toMinutesPart()<10)?"0"+dc.toMinutesPart():String.valueOf(dc.toMinutesPart());
        if (timeCar < timeTrain) System.out.println("CAR "+dc.toHoursPart()+":"+minutesc);
        else System.out.println("TRAIN "+dt.toHoursPart()+":"+minutest);
    }

	private static StepList buildCorrectRoute(StepList allSteps, String startingPoint, String destination) {
		StepList returnVal = new StepList();
		Step nextStep = null;
		for(Step s:allSteps) {
        	if(s.from.equals(startingPoint)) {
        		returnVal.add(s);
        		nextStep = s;
        	}
        }
		while(!destination.equals(nextStep.to)) {
			for(Step s:allSteps) {
				if(s.from.equals(nextStep.to)) {
					returnVal.add(s);
					nextStep=s;
				}
			}
		}
		return returnVal;
	}

}
