using System; using System.Linq;using System.IO;using System.Text;using System.Collections;using System.Collections.Generic;

class Solution
{

	public static List<FriendClass> Friends = new List<FriendClass>();

    public static char[,] Grid;
    public static List<Point>Points = new List<Point>();
    public static List<string> inputList = new List<string>();
    public static Dictionary <char, List<Point>> dic = new Dictionary <char, List<Point>>();
    public static Dictionary <string, Point> ThePointsDic = new Dictionary <string, Point>();
    public static List<string> AllPatternsForSingleWord = new List<string>();

	public static int siz = 4;
	public static string bl = " ";

	public static string[] FriendsRay = "Joey Rachel Ross Monica Chandler Phoebe".Split(' ');
	public static string[] FewFriendsRay;
