	public static bool IsThisWordValid(string word)
	{
		string wordPattern = FindWordPattern(word);
		if (wordPattern.Contains("0"))
		{
			return false;
		}
		AllPatternsForSingleWord = GetAllPatternsForSingleWord(word,wordPattern);

		int lowestNumberOfFailedPairs = 99;//this works fine; dont sweat it

		foreach(var aSinglePattern in AllPatternsForSingleWord)
		{
			List<Point> sop =  CreateSeriesOfPoints(word, aSinglePattern);
			int numOfFailedPairsForThisPattern = WillReturnZeroIfValid(sop); // for this single pattern 
			if (numOfFailedPairsForThisPattern < lowestNumberOfFailedPairs) lowestNumberOfFailedPairs = numOfFailedPairsForThisPattern;
		}
		if (lowestNumberOfFailedPairs == 0) return true;
		return false;
	}


    public static List<Point> CreateSeriesOfPoints(string word, string particularWordPattern)
    {
        int leng = word.Length;
        List<Point> thesePoints = new List<Point>();
        for (int pos = 0 ; pos < leng ; pos++ )
        {
            char ch = word[pos];
            char charOption = particularWordPattern[pos];
            int intOption = charOption - '0'; 
            int indexIntOption = intOption - 1;
            Point pt = dic[ch][indexIntOption]; 
            thesePoints.Add(pt);
        }
        return thesePoints;
    }

	public static int WillReturnZeroIfValid(List<Point> seriesOfPoints) 
    {
        MakeAllPointsAvailableAgain();
        int leng = seriesOfPoints.Count();

		int actualNumOfSuccessPairs = 0;
		int goalNumOfSuccessPairs = leng - 1;

        int BI = leng - 1;
        for (int whichPt = 0 ; whichPt < BI ; whichPt++ )
        {
            var PointA = seriesOfPoints[whichPt];            
            var PointB = seriesOfPoints[whichPt + 1];
            bool pairAreAdj = Point.Are2PointsAdjacent(PointA, PointB); // it also checks, and only returns true if PointB is available

            if (pairAreAdj == true) 
			{
				PointA.Available = false;
            	PointB.Available = false;
				actualNumOfSuccessPairs++;
			}
        }
		return goalNumOfSuccessPairs - actualNumOfSuccessPairs; // so if it's almost right, this will be 1 (very low)
		// like if it should be 6 but it's only 5 matches, this will return 1;
    }


    public static void MakeAllPointsAvailableAgain()
    {
        foreach (var pt in Points)
        {
            pt.Available = true;
        }
    }

    public static string FindWordPattern(string word) 
    /*  so like if work is flood 
       -- and there are 2 "f"
                        1 "l"
                        1 "o"
                    and 3 "d"
     -- it would be 21113.   Of course this won't work if there are 10+ of a single letter but...
    ...that's okay because the boggle board cubes are such that that could never possibly happen.  
    In other words fewer than 10 of the cubes have any specific letter on them. 

    */
    {
        int leng = word.Length;
        string wordPattern = "";
        for (int pos = 0 ; pos < word.Length ; pos++)
        {
            char ch = word[pos];
            if (dic.ContainsKey(ch))
            {
                List<Point> tempList = dic[ch];
                wordPattern += tempList.Count;
            }
            else
            {
                wordPattern += '0';
            }
        }
        return wordPattern;
    }

    public static void PopulatePointsAndDic()
    {
        for (int lin = 0 ; lin < 4; lin++)
        {
            for (int pos = 0 ; pos < 4 ; pos++)
            {
                char ch = Grid[lin,pos];
                Point thisPt = new Point(lin,pos);
                Points.Add(thisPt);
                ThePointsDic.Add(lin+""+pos,thisPt);

                if (dic.ContainsKey(ch))
                {
                    dic[ch].Add(thisPt);
                }
                else
                {
                    List<Point> newList = new List<Point>();
                    newList.Add(thisPt);
                    dic.Add(ch,newList);
                }
            }
        }
    }


	public static void PopulateGrid(string members)
    {

		for (int lin = 0 ; lin < 4; lin++)
        {
            for (int pos = 0 ; pos < 4 ; pos++)
            {
				int indexInLettersShowing = lin*4 + pos;
                Grid[lin,pos] =  members[indexInLettersShowing];
            }
        }
    }

    public static List<string> GetAllPatternsForSingleWord(string word, string wordPattern) /// so like for wordPattern 321, it would return 111 121 211 221 311 321
    {
        List<string> list = new List<string>();
        int leng = word.Length;
        int BI = leng - 1;
        string strMaxx = wordPattern;
        string strCurr = new String('1', leng);
        list.Add(strCurr);

        var maxx = strMaxx.ToCharArray();

        while (strCurr != strMaxx)
        {
            var curr = strCurr.ToCharArray();
            curr[BI] = AddOne(curr[BI]);
            for (int pos = BI ; pos > 0 ; pos--)
            {
                if (curr[pos] > maxx[pos])
                {
                    curr[pos] = '1';
                    curr[pos-1] = AddOne(curr[pos-1]);
                }
            }
            strCurr = String.Join("",curr);
            list.Add(strCurr);
        }
        return list;
    }

    public static char AddOne(char ch)
    {
        int i = (int)ch;
        i++;
        return (char)i;
    }


	// ============================================================================ Point CLASS
    public class Point
    {
        public int Lin;
        public int Pos;
        public bool Available = true;
        public string Name;

        public Point(int lin, int pos)
        {
            Lin = lin;
            Pos = pos;
            Name = Lin + "" + Pos;
        }

        public static bool Are2PointsAdjacent(Point p1, Point p2) // also checks if next one is available
        {
            int diffLin = Math.Abs(p1.Lin - p2.Lin);
            int diffPos = Math.Abs(p1.Pos - p2.Pos);
            bool bo1 = diffLin <= 1 && diffPos <= 1;
			bool bo2 = diffLin + diffPos != 0; // its different points.  Yes, must have this
            bool bo3 = ThePointsDic[p2.Lin + "" + p2.Pos].Available == true;
            bool bo4 = bo1 && bo2 & bo3;
            if (bo4) return true;
            return false;
        }
    }

	public class FriendClass 	// ============================================================================Friend CLASS
	{
		public string Name;
		public List<PlayedWordClass> ThisPlayersWords = new List<PlayedWordClass>();
		public int TotalValue = 0 ;


		public FriendClass(string n, List<string> wordList)
		{
			Name = n;
			foreach (var w in wordList)
			{
				bool alreadyWroteIt = false;
				foreach (var aWord in ThisPlayersWords)
				{
					if (aWord.ActualWord == w) alreadyWroteIt = true;
				}
				if (alreadyWroteIt == false)
				{
					var temp = new PlayedWordClass(w);
					ThisPlayersWords.Add(temp);
				}
				else
				{
					var temp = new PlayedWordClass(w,true); // "true" if is duplicate within list
					ThisPlayersWords.Add(temp);
				}
			}
		}

		public void CalcTotalValue()
		{

			foreach  (var w in ThisPlayersWords)
			{
				if (w.IsUnique && w.IsValidForThisBoggleBoard && w.IsDuplicateInList == false)
				{
					w.ActualValue = w.PotentialValue;
				}
			}

			TotalValue = 0;
			foreach  (var w in ThisPlayersWords)
			{
				TotalValue += w.ActualValue;
			}
		}
	}


	public class PlayedWordClass  	// ============================================================================ PlayedWord CLASS
	{
		public string ActualWord;
		public bool IsValidForThisBoggleBoard = false; //until proven other wise
		public bool IsDuplicateInList = false;
		public bool IsUnique = false;

		public int PotentialValue = 0;
		public int ActualValue = 0;


		public PlayedWordClass(string strWord, bool isDup = false) // constructor
		{
			ActualWord = strWord;
			if (isDup == true) 
			{
				IsDuplicateInList = true;
			}
			if (IsThisWordValid(ActualWord))
			{
				IsValidForThisBoggleBoard = true;
			}
			CalcValue();
		}

		public void CalcValue()
		{
			/*
			3-letter words and 4-letter words are 1 point each.
			5-letter words are 2 points each.
			6-letter words are 3 points each.
			7-letter words are 5 points each.
			8-letter words and longer are 11 points each.
			*/
			int len = ActualWord.Length;

			switch(len) 
			{
				case 3: PotentialValue = 1; break;
			  	case 4: PotentialValue = 1; break;
				case 5: PotentialValue = 2; break;
				case 6: PotentialValue = 3; break;
				case 7: PotentialValue = 5; break;
				default: PotentialValue = 11; break;
			}
		}

		public void PrintWord()
		{
            string s = String.Format("{0,15} {1,15} {2,15} {3,30} {4,15} {5,15}", ActualWord , IsValidForThisBoggleBoard ,IsDuplicateInList , IsUnique , PotentialValue ,ActualValue);
			Console.WriteLine(s);
		}

		public void PrintWordWithValue()
		{
			if (ActualValue > 0)
			{
				string s = ActualValue + bl + ActualWord;
				Console.WriteLine(s);
			}
		}
	}
}
