let parkLots = [];

//0 -> Not calibrated
//1 -> Calibration Active
//2 -> Calibrated
let calibration = 0;
let cLength = 0; L = 0; R = 0;
const ParkPilot = (sensData, i) => {
    const [FL, FR, BR, BL] = sensData.split('').map(Number).map(Boolean);
    if (calibration == 0) {
        if (!FL) {
            cLength += 1;
            calibration = 1;
        }
    }
    else if (calibration == 1) {
        cLength += 1;
        if (!BL) calibration = 2;
    }
    else if (calibration == 2) {
        L = FL ? 0 : L + 1;
        R = FR ? 0 : R + 1;

        if (L >= cLength) parkLots.push({ i: i + (cLength - 1), s: "L" })
        if (R >= cLength) parkLots.push({ i: i + (cLength - 1), s: "R" })
    }
}

const N = parseInt(readline()); // Road length
for (let i = 0; i < N; i++) {
    const sensorData = readline(); // Datas from four sensor with values 1 or 0 (e.g 1001)
    ParkPilot(sensorData,i)
}
console.log(cLength);
