l = int(input())
w = int(input())
lowc=lowr = 20
highc=highr  = 0
grid = []
for i in range(l):
    row = input()
    grid.append(row)
    if '#' in row:
        if i<lowr: lowr = i
        if i> highr: highr = i
        if row.find('#') < lowc: lowc = row.find('#')
        most_right = w - (row[::-1].find('#')+1)
        if most_right > highc: highc = most_right
allC = list(range(lowc,highc+1))
allR = list(range(lowr,highr+1))
for rowi in range(l):
    for coli in range(w):
        if (rowi in allR and coli in [lowc,highc]) or (coli in allC and rowi in [lowr,highr]):
            if grid[rowi][coli] != '#':
                print(rowi,coli)
                break
