using System;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        const char SPACE = ' ';
        string framePattern = Console.ReadLine();
        var frameSize = framePattern.Length;
        string[] inputs = Console.ReadLine().Split(' ');
        int h = int.Parse(inputs[0]); // the height of the picture

        var picture = new List<string>();
        var pictureWidth = 0;

        for (var i = 0; i < h; i++)
        {
            var line = Console.ReadLine();
            if (pictureWidth < line.Length)
                pictureWidth = line.Length;

            picture.Add(line);
        }

        var totalWidth = pictureWidth + 2 + 2 * frameSize;
        var topBottom = new List<string>();

        for (var i = 0; i < frameSize + 1; i++) 
        {
            var f = i < frameSize ? framePattern[i] : SPACE;
            var line = new string(f, totalWidth - i * 2);

            var padding = framePattern.Substring(0, i);
            line = line.PadCenter(padding);
            Console.WriteLine(line);

            topBottom.Insert(0, line);
        }

        for (var i = 0; i < h; i++)
        {
            var line = picture[i].PadRight(pictureWidth).PadCenter(framePattern + SPACE);
            Console.WriteLine(line);
        }

        foreach (var line in topBottom) 
            Console.WriteLine(line);
    }
}

public static class StringExtensions
{
    public static string PadCenter(this string str, string padding)
    {
        return padding + str + padding.Reverse();
    }

    public static string PadCenter(this string str, char padding)
    {
        return padding + str + padding;
    }

    public static string Reverse(this string str)
    {
        if (str.Length <= 1)
            return str;

        char[] chars = str.ToCharArray();
        Array.Reverse(chars);
        return new string(chars);
    }
}
