cards = ["2", "3", "4", "5", "6", "7", "8", "9", "10", "Jack", "Queen", "King", "Ace"]

player_1 = cards.index(input())
player_2 = cards.index(input())

dist = abs(player_2 - player_1)

if player_1 > player_2:
    print("Player1")
elif player_1 < player_2:
    print("Player2")

if dist == 0: print("War")
elif dist >= 1 and dist <= 3: print("Really Good")
elif dist >= 4 and dist <= 6: print("Good")
elif dist >= 7 and dist <= 9: print("Bad")
elif dist >= 10 and dist <= 12: print("Really Bad")

